mapper.insert:

    select VOLUNTEER_PROCEED_SEQ.NEXTVAL VOLUN_PROCEED_SEQ from dual


INSERT INTO volunteer_proceed (
	VOLUN_PROCEED_SEQ
    ,RECRUIT_SEQ
    ,MEM_SEQ
    ,GROUP_MEM_SEQ
) VALUES (
    #{volunProceedSeq}
    ,#{recruitSeq}
    ,#{memSeq}
    ,#{groupMemSeq}
)

mapper.select:
SELECT COUNT(*)
FROM volunteer_proceed
WHERE 
	RECRUIT_SEQ = #{recruitSeq}
AND
	MEM_SEQ = #{memSeq}

